commands.command:

INSERT INTO dbo.Cost
        ( ID ,
          SheetID ,
          Type ,
          Amount ,
          UnitPrice ,
          Total ,
          Status ,
          Remark ,
          CreateTime ,
          LastModifyTime ,
          LastModifyUser 
        )
VALUES  ( NEWID() ,
          @SheetID ,
          @Type ,
          @Amount ,
          @UnitPrice ,
          @Total ,
          @Status , 
          @Remark ,
          GETDATE() ,
          GETDATE() ,
          @LastModifyUser
        )
