commands.command:

INSERT INTO dbo.Received
        ( ID ,
          SheetID ,
          Money ,
          Time ,
          CreateTime ,
          LastModifyTime ,
          LastModifyUser 
        )
VALUES  ( NEWID() ,
          @SheetID ,
          @Money ,
          @Time , 
          GETDATE() ,
          GETDATE(),
          @LastModifyUser
        )
