commands.command:

INSERT INTO dbo.Sheet
        ( ID ,
          UserID ,
          AuditStatus ,
          PayStatus ,
          CustomName ,
          Contacts ,
          Phone ,
          QQ ,
          WeiXin ,
          Address ,
          TimeFrom ,
          TimeTo ,
          Base ,
          People ,
          Days ,
          Source ,
          Total ,
          Unit ,
          Cost ,
          Received ,
          Remaining ,
          Commission,
          [Percent],
          [TaxRate],
          [Tax],
          [Achievement],
          Remark ,
          CreateTime ,
          LastModifyTime ,
          LastModifyUser 
        )
VALUES  ( @ID ,
          @UserID ,
          @AuditStatus,
          @PayStatus ,
          @CustomName ,
          @Contacts ,
          @Phone,
          @QQ,
          @WeiXin,
          @Address,
          @TimeFrom,
          @TimeTo,
          @Base,
          @People ,
          @Days,
          @Source ,
          @Total , 
          @Unit ,
          @Cost , 
          @Received ,
          @Remaining ,
          @Commission,
          @Percent,
          @TaxRate,
          @Tax,
          @Achievement,
          @Remark,
          GETDATE(),
          GETDATE(),
          @LastModifyUser 
        )
